from flask import Flask,render_template,requestimport picklemodel = pickle.load(open('SpamOrHam.pkl','rb'))counterVector = pickle.load(open('CounterVector.pkl','rb')) app = Flask(__name__)@app.route('/')def hello_world():   return render_template("index.html")@app.route('/detect', methods = ['GET','POST'])def detectEmails():        if request.method == "POST" :        message = request.form['msg']        vect = counterVector.transform([str(message)])        status = model.predict(vect)                if status[0] == 1:            msg =  " Congrats!! Email is not Spam"        else :            msg =   " Oops!! You Entered a Spam Email"                else :        msg = ""        message = ""        return render_template("index.html", result = msg, prevMsg = message)    #return "Hello"if __name__ == '__main__':   app.run(debug = True)